<?php  /** @var LayoutView $this */  $bag = $this->Bag;  $strings = $this->LayoutStrings;  $page_title = $strings::APPLICATION_NAME . ': ' . $bag->headerTitle;  $messageBoxClass = isset($bag->messageBox) ? ' class="show-message-box"' : '';?><!DOCTYPE html><html><head>  <meta charset="utf-8" http-equiv="X-UA-Compatible" content="IE=edge" />  <title><?=$page_title?></title><?php foreach ($bag->stylesheets as $stylesheetUri): ?>  <link rel="stylesheet" type="text/css" href="<?="$stylesheetUri?".Settings::VERSION?>" /><?php endforeach ?><?php foreach ($bag->scripts as $scriptUri): ?>  <script type="text/javascript" src="<?="$scriptUri?".Settings::VERSION?>" ></script><?php endforeach ?></head><body<?=$messageBoxClass?>><?php if ($messageBoxClass): $bag->messageBox->Render(); endif ?><div class="header">  <div class="header-item header-item-left"><?=$bag->headerTitle?></div><?php foreach ($bag->headerItems as $link): ?>  <div class="header-item"><a href="<?=$link->uri?>"><?=$link->text?></a>|</div><?php endforeach ?><?php if ($bag->languageItems): ?>  <div class="header-item">    <ul id="language_menu" class="drop-menu">      <li>        <img class="flag-image" src="/views/layout/img/flags/<?=$bag->languageItems[0]->code?>.png" /><span><?=$bag->languageItems[0]->name?></span>        <ul><?php for ($i = 1, $count = count($bag->languageItems); $i < $count; $i++): ?>            <li>              <a href="<?=$bag->languageItems[$i]->uri?>">                <img class="flag-image" src="/views/layout/img/flags/<?=$bag->languageItems[$i]->code?>.png" /><span><?=$bag->languageItems[$i]->name?></span>              </a>            </li><?php endfor ?>        </ul>      </li>    </ul>  </div><?php endif ?></div><?php $this->RenderBody() ?></body></html>