<?php/** @var LayoutView $this */$strings = $this->LayoutStrings;$alert = $this->Alert;$page_title = $strings::APPLICATION_NAME . ': ' . $this->HeaderTitle;?><!DOCTYPE html><html><head>  <meta http-equiv="X-UA-Compatible" content="IE=edge" charset="utf-8"/>  <meta http-equiv="Content-Type" content="text/html; charset=utf-8"/>  <title><?=$page_title?></title><?php foreach ($this->Stylesheets as $stylesheetUri): ?>  <link rel="stylesheet" type="text/css" href="<?="$stylesheetUri?".Settings::VERSION?>" /><?php endforeach ?><?php foreach ($this->Scripts as $scriptUri): ?>  <script type="text/javascript" src="<?="$scriptUri?".Settings::VERSION?>" ></script><?php endforeach ?></head><body><?php if ($this->MessageBoxRequired): ?><script type="text/javascript">  var messageBox = new MessageBox(<?=MessageBox::GetButtonTitles($strings->GetLanguage())?>);<?php if ($alert): ?><?php if ($this->FocusedElement): ?>  messageBox.show('<?=$alert->Text?>', <?=$alert->Type?>, <?=$alert->Buttons?>, function() {    focusElementByName('<?=$this->FocusedElement?>');  });<?php else: ?>  messageBox.show('<?=$alert->Text?>', <?=$alert->Type?>, <?=$alert->Buttons?>);<?php endif ?><?php endif ?></script><?php endif ?><div class="header">  <div class="header-item header-item-left"><?=$this->HeaderTitle?></div><?php foreach ($this->HeaderItems as $link): ?>  <div class="header-item"><a href="<?=$link->Uri?>"><?=$link->Text?></a>|</div><?php endforeach ?><?php if ($this->LanguageItems): ?>  <div class="header-item">    <ul id="language_menu" class="drop-menu">      <li onclick="">        <img class="flag-image" src="/views/layout/img/flags/<?=$this->LanguageItems[0]->Code?>.png" /><span><?=$this->LanguageItems[0]->Name?></span>        <ul><?php for ($i = 1, $count = count($this->LanguageItems); $i < $count; $i++): ?>            <li>              <a href="<?=$this->LanguageItems[$i]->Uri?>">                <img class="flag-image" src="/views/layout/img/flags/<?=$this->LanguageItems[$i]->Code?>.png" /><span><?=$this->LanguageItems[$i]->Name?></span>              </a>            </li><?php endfor ?>        </ul>      </li>    </ul>  </div><?php endif ?></div><?php $this->RenderBody() ?><?php if (!$alert && $this->FocusedElement): ?><script type="text/javascript">focusElementByName('<?=$this->FocusedElement?>');</script><?php endif ?></body></html>